import React, {useState, useEffect} from 'react'
import {useHistory} from 'react-router-dom'
import '../styles/SignUp.css'

const Profile = ({currentUser}) => {
    //TODO: 
    //how to make this render when updated? 
    //icons on page 
    //bootstrap for login component
    //api for our the website (payment/what are we selling)
    const [userData, setUserData] = useState(currentUser)
    const history = useHistory()

    useEffect(()=> { //how to apply this
        setUserData(userData)
    }, [userData])

    const profileEdit = () => {
        history.push('/profile/edit')
    } 
a
    const {username, first_name, last_name, email, address} = userData.data

    if(!currentUser){
        history.push('/access/error')
    }
    return (
        <main className="SignUp-form card">
        <div className="text-center">
            <div className="text-center">
                <h1 className="SignUp-create-account h2 mb-2 fw-normal">{username} Profile</h1>
                <p><b>First Name:</b> {first_name === null ? <i>First Name</i> : setUserData(first_name)}</p>
                <p><b>Last Name:</b> {last_name === null ? <i>Last Name</i> : setUserData(last_name)}</p>
                <p><b>Email:</b> {email === null ? <i>Email Address</i> : setUserData(email)}</p>
                <p><b>Address:</b> {address === null ? <i>Shipping Address</i> : setUserData(address)}</p>
                <div className="mt-3">
                <button className="w-100 btn btn-primary" type="submit" onClick={profileEdit}>Edit Profile</button>
                </div>
        </div>
    </div>
    </main>
    )
}

export default Profile
