import React from 'react'
import Lottie from 'react-lottie'
import '../styles/loading.css'

const LoadSpinner = () => {

    //download react lottie instead and use component to render.

    const defaultOptions = {
        loop: true,
        autoplay: false,
        animationData: require('./paperLoading.json'), // the path to the animation json
        rendererSettings: {
            preserveAspectRatio: 'xMidYMid slice'
        }
        };

    return (
        <div>
            <Lottie options={defaultOptions}/>
        </div>
    )
}

export default LoadSpinner
