import axios from 'axios'
import {API_KEY} from '../../config'

const BASE_URL = "https://api.spoonacular.com" 

const tags = ["mediterranean", "italian", "european", "greek"] //put in an array and randomize to come out as string in commons


const shuffleTags = (tags) => {
    const sortTags = tags.sort(() => Math.random() - 0.5)
    sortTags.find((tag) => {
        return tag
    })
}

const ourTags = shuffleTags(tags)

class Products {
    static async getRandomMeal() {
        const res = await axios.get(`${BASE_URL}/recipes/random?tags=${ourTags}&apiKey=${API_KEY}`)
        return res
    }

    //random wine make sure to put type to match the random meal if there type on random meal api find it
    static async getPairWine() {
        const res = await axios.get(`https://api.spoonacular.com/food/wine/pairing?food=mediterranean&apiKey=${API_KEY}`)
        return res
    }
}

export default Products
