import React, {useState} from 'react'
import {useHistory} from 'react-router-dom'
import '../styles/SignUp.css'

const Profile = ({currentUser}) => {
    //TODO: 
    //how to make this render when updated? 
    //icons on page 
    //bootstrap for login component
    //api for our the website (payment/what are we selling)

    //how to pass in editdata to setUserData to be from ProfileEdit // how to update the currentUser with state?

    const [userFirst, setUserFirst] = useState(currentUser.data.first_name)
    const [userLast, setUserLast] = useState(currentUser.data.last_name)
    const [userEmail, setUserEmail] = useState(currentUser.data.email)
    const [userAddress, setUserAddress] = useState(currentUser.data.address)
    const history = useHistory()

    const profileEdit = () => {
        history.push('/profile/edit')
    } 

    // const {username, first_name, last_name, email, address} = currentUser.data

    if(!currentUser){
        history.push('/access/error')
    }

    //create a map through the 
    return (
        <main className="SignUp-form card">
        <div className="text-center">
            <div className="text-center">
                <h1 className="SignUp-create-account h2 mb-2 fw-normal">{username} Profile</h1>
                <p><b>First Name:</b> {userFirst === null ? <i>First Name</i> : setUserFirst(userFirst)}</p>
                <p><b>Last Name:</b> {userLast === null ? <i>Last Name</i> : setUserLast(userLast)}</p>
                <p><b>Email:</b> {userEmail === null ? <i>Email Address</i> : setUserEmail(userEmail)}</p>
                <p><b>Address:</b> {userAddress === null ? <i>Shipping Address</i> : setUserAddress(userAddress)}</p>
                <div className="mt-3">
                <button className="w-100 btn btn-primary" type="submit" onClick={profileEdit}>Edit Profile</button>
                </div>
        </div>
    </div>
    </main>
    )
}

export default Profile
