import Lottie from 'react-lottie'
import '../styles/loading.css'

const LoadSpinner = () => {
    //Todo: pick a loading animation// theme color // resize/center in the middle? 
    //Find navbar/brand icon 
    //api for our the website (payment/what are we selling
    const defaultOptions = {
        loop: true,
        autoplay: true,
        animationData: require('./gearLoading.json'), // the path to the animation json
        rendererSettings: {
            preserveAspectRatio: 'xMidYMid slice'
        }
        };

    //resizing 
    return (
        <div>
            <Lottie options={defaultOptions} height={200} width={200}/>
            <p>....</p>
        </div>
    )
}

export default LoadSpinner
