import React, {useContext} from 'react'
import AppContext from './AppContext'
import {NavLink} from 'react-router-dom'
import Lottie from 'react-lottie'
import './styles/global.css'

const Navbar = () => {
    const {currentUser, logout} = useContext(AppContext)
    //Todo: 
    //fix this on animation
    //hover on links turn logo color
    //Find navbar/brand icon 
    //api for our the website (payment/what are we selling


    const avatarLogo = {
        loop: false,
        autoplay: true,
        animationData: require('./logos/avatarLogo.json'), // the path to the animation json
        rendererSettings: {
            preserveAspectRatio: 'xMidYMid slice'
        }
    };

    return (
        <main>
        <nav className="navbar navbar-light bg-light">
            <div className="container-fluid">
                <NavLink className="navbar-brand" to="/">BuyMe</NavLink>

                {/* if logged in, render username with dropdown else signup/login routes */}
                {currentUser ? 
                <ul className="nav justify-content-end">
                    <div className="btn-group">
                    <li className="nav-item  dropdown-toggle" type="button" data-bs-toggle="dropdown">
                        {currentUser.data.username}
                        <Lottie options={avatarLogo} height={40} width={40} style={{display: "inline-block"}}/>
                    </li>
                    <ul className="dropdown-menu dropdown-menu-end dropdown-menu-lg-start">
                        <li><NavLink className="dropdown-item" to="/profile">Account</NavLink></li>
                        <li className="dropdown-item" onClick={logout}>Logout</li>
                    </ul>
                    </div>
                </ul>

                :
                <ul className="nav justify-content-end">
                    <li className="nav-item">
                        <NavLink className="Navbar-color m-2" to="/signup">Sign Up</NavLink>
                    </li>
                    <li className="nav-item">
                        <NavLink className="Navbar-color" to="/login">
                            Login
                        </NavLink>
                    </li>
                </ul>
                }  
            </div>
        </nav>
        </main>
    )
}

export default Navbar
