import React, {useContext} from 'react'
import {Elements} from '@stripe/react-stripe-js';
import {loadStripe} from '@stripe/stripe-js';
import { PUBLIC_KEY } from '../../config';
import AppContext from '../../AppContext'
import CheckoutForm from './CheckoutForm';
import Payment from './paymentApi';

const stripePromise = loadStripe(PUBLIC_KEY);

const StripeContainer = () => {
    const {currentUser} = useContext(AppContext)
    const mealId = JSON.stringify(localStorage.getItem('signature-meal').mealId)
    //create purchase functions API here?
    console.log(mealId)

    const makeSignatureMealPurchase = async (userId, mealId) => {
        const res = await Payment.signatureMealPurchase(userId, mealId)
        return res
    }

    return (
        <Elements stripe={stripePromise}>
            <CheckoutForm user={currentUser} meal={makeSignatureMealPurchase}/>
        </Elements>
    )
}

export default StripeContainer
