import {SQUARE_ACCESS_TOKEN} from '../config'
import { Client, Environment } from 'square'
import { v4 as uuidv4 } from 'uuid';
import axios from 'axios'

// const BASE_URL = "https://connect.squareupsandbox.com/v2/payments"

const client = new Client({
    environment: Environment.Sandbox,
    accessToken: "EAAAEKtsRZ3co5jeY_9BlckvYbF5Y2iwG4uKu5vVQ5rFXvxrkPjAW7nuVULbcFk7",
})

class Square {

    static async createPayments() {
        try {
            const response = await client.paymentsApi.createPayment({
                sourceId: 'cnon:card-nonce-ok',
                idempotencyKey: uuidv4(),
                amountMoney: {
                amount: 9,
                currency: 'USD'
                }
            });

            console.log(response.result);
            } catch(error) {
            console.log(error);
        }
}
}

export default Square
