import React, {useState, useContext, useEffect} from 'react'
import {Elements} from '@stripe/react-stripe-js';
import {loadStripe} from '@stripe/stripe-js';
import { PUBLIC_KEY } from '../../config';
import AppContext from '../../AppContext'
import CheckoutForm from './CheckoutForm';
import Payment from './paymentApi';
import Products from '../foodApi'

const stripePromise = loadStripe(PUBLIC_KEY);

const StripeContainer = () => {
    const {currentUser} = useContext(AppContext)
    const [mealId, setMealId] = useState(null)
    const [pairMealId, setPairMealId] = useState(null)

    useEffect(()=> {
        const mealIdSetUp = async() => {
        const signatureMeal = await Products.getSignatureMeal()
        const pairMeal = await Products.getPairMeal()
        const signatureMealId = signatureMeal.data[0].id
        const pairMealId = pairMeal.data[0].id
        setMealId(signatureMealId)
        setPairMealId(pairMealId)
        }
        mealIdSetUp()
    })

    // const getSignatureMeal = async () => {
    //     const signatureMeal = await Products.getSignatureMeal()
    //     const signatureMealId = signatureMeal.data[0].id
    //     setMealId(signatureMealId)
    // }

    // const getPairMeal = async () => {
    //     const pairMeal = await Products.getPairMeal()
    //     const pairMealId = pairMeal.data[0].id
    //     setPairMealId(pairMealId)
    // }

    return (
        <Elements stripe={stripePromise}>
            <CheckoutForm user={currentUser} mealId={mealId}/>
        </Elements>
    )
}

export default StripeContainer
