import axios from 'axios'
import {API_KEY} from '../../config'

const BASE_URL = "https://api.spoonacular.com" 

class Products {
    static async getRandomMeal() {
        const res = await axios.get({
            baseURL: BASE_URL,
            headers: {'X-Custom-Header': API_KEY}
        })
        return res
    }

    //random wine make sure to put type to match the random meal if there type on random meal api find it
    static async getRandomWine() {
        const res = await axios.get(`https://api.spoonacular.com/food/wine/description?wine=merlot?apiKey=${API_KEY}`)
        return res
    }
}

export default Products
