import React, {useContext, useState} from 'react'
import AppContext from '../AppContext'
import {Redirect} from 'react-router-dom'
import User from '../api'
import jwt from "jsonwebtoken";

const Profile = () => {
    const {currentUser, token} = useContext(AppContext)
    const [code, setCode] = useState(token)

    const tokenDecode = () => {
        const userDecoded = jwt.decode(token)
        setCode(userDecoded.id)
    }
    // if(!currentUser){
    //     <Redirect to="/login"/>
    // }
    //only currentUser can access this page.
    //currentUser in order to access this page. Create API request from backend
    //add api how to edit
    //how to delete user as well.


    return (
        <div>
            <p>Profile Page</p>
            <p onClick={tokenDecode}>{code}</p>
        </div>
    )
}

export default Profile
