import axios from 'axios'
import {API_KEY} from '../../config'
import shuffleTags from '../commons/shuffleTags'

const BASE_URL = "https://api.spoonacular.com" 

const tags = ["mediterranean", "italian", "european", "greek", "chinese,", "asian", "cajun", "creole", "american", "Thai"]

const randMeal = shuffleTags(tags)

class Products {
    static async getRandomMeal() {
        const res = await axios.get(`${BASE_URL}/recipes/random?tags=${randMeal}&apiKey=${API_KEY}`)
        return res
    }

    //random wine make sure to put type to match the random meal if there type on random meal api find it
    static async getPairWine() {
        const res = await axios.get(`https://api.spoonacular.com/food/wine/pairing?food=mediterranean&apiKey=${API_KEY}`)
        return res
    }
}

export default Products
